// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Crowdfunding {
    struct Campaign {
        address payable creator;
        uint256 goal;
        uint256 pledged;
        uint256 startTime;
        uint256 endTime;
        bool claimed;
        mapping(address => uint256) pledges;
        mapping(address => bool) hasVoted;
        uint256 yesVotes;
        uint256 noVotes;
        bool votingOpen;
        bool exists;
    }

    mapping(uint256 => Campaign) public campaigns;
    uint256 public campaignCount;

    event CampaignCreated(uint256 id, address creator, uint256 goal, uint256 startTime, uint256 endTime);
    event PledgeReceived(uint256 id, address contributor, uint256 amount);
    event VotingOpened(uint256 id);
    event Voted(uint256 id, address voter, bool vote);
    event FundsClaimed(uint256 id);
    event FundsRefunded(uint256 id, address contributor, uint256 amount);

    modifier campaignExists(uint256 _id) {
        require(campaigns[_id].exists, "Campaign does not exist");
        _;
    }

    modifier campaignActive(uint256 _id) {
        require(block.timestamp <= campaigns[_id].endTime, "Campaign has ended");
        _;
    }

    function createCampaign(uint256 _goal, uint256 _durationInDays) public {
        require(_goal > 0, "Goal must be greater than 0");
        require(_durationInDays > 0, "Duration must be greater than 0");

        campaignCount++;
        Campaign storage newCampaign = campaigns[campaignCount];

        newCampaign.creator = payable(msg.sender);
        newCampaign.goal = _goal;
        newCampaign.startTime = block.timestamp;
        newCampaign.endTime = block.timestamp + (_durationInDays * 1 days);
        newCampaign.exists = true;
        newCampaign.claimed = false;
        newCampaign.votingOpen = false;

        emit CampaignCreated(
            campaignCount, 
            msg.sender, 
            _goal, 
            newCampaign.startTime, 
            newCampaign.endTime
        );
    }

    function pledge(uint256 _id) public payable campaignExists(_id) campaignActive(_id) {
        Campaign storage campaign = campaigns[_id];
        require(msg.value > 0, "Pledge amount must be greater than 0");
        require(!campaign.claimed, "Campaign funds have been claimed");

        uint256 remainingAmount = campaign.goal - campaign.pledged;
        require(remainingAmount > 0, "Campaign goal already reached");

        // If msg.value would complete the goal, require exact remaining amount
        if (campaign.pledged + msg.value >= campaign.goal) {
            require(msg.value == remainingAmount, "Please invest exact remaining amount");
        } else {
            // For partial investments, just ensure it doesn't exceed the goal
            require(campaign.pledged + msg.value <= campaign.goal, "Investment would exceed campaign goal");
        }

        campaign.pledged += msg.value;
        campaign.pledges[msg.sender] += msg.value;
        emit PledgeReceived(_id, msg.sender, msg.value);
    }

    function openVoting(uint256 _id) public campaignExists(_id) {
        Campaign storage campaign = campaigns[_id];
        require(msg.sender == campaign.creator, "Only creator can open voting");
        require(campaign.pledged >= campaign.goal, "Goal not reached");
        require(!campaign.votingOpen, "Voting already open");
        require(!campaign.claimed, "Funds already claimed");

        campaign.votingOpen = true;
        emit VotingOpened(_id);
    }

    function vote(uint256 _id, bool _vote) public campaignExists(_id) {
        Campaign storage campaign = campaigns[_id];
        require(campaign.votingOpen, "Voting not open");
        require(campaign.pledges[msg.sender] > 0, "Only contributors can vote");
        require(!campaign.hasVoted[msg.sender], "Already voted");
        require(!campaign.claimed, "Funds already claimed");

        if (_vote) {
            campaign.yesVotes++;
        } else {
            campaign.noVotes++;
        }
        campaign.hasVoted[msg.sender] = true;

        emit Voted(_id, msg.sender, _vote);
    }

    function claimFunds(uint256 _id) public campaignExists(_id) {
        Campaign storage campaign = campaigns[_id];
        require(msg.sender == campaign.creator, "Only creator can claim funds");
        require(campaign.pledged >= campaign.goal, "Goal not reached");
        require(campaign.votingOpen, "Voting not opened");
        require(campaign.yesVotes > campaign.noVotes, "Vote unsuccessful");
        require(!campaign.claimed, "Funds already claimed");

        campaign.claimed = true;
        campaign.creator.transfer(campaign.pledged);

        emit FundsClaimed(_id);
    }

    function refund(uint256 _id) public campaignExists(_id) {
        Campaign storage campaign = campaigns[_id];
        require(block.timestamp > campaign.endTime, "Campaign has not ended");
        require(campaign.pledged < campaign.goal, "Goal was reached");
        require(!campaign.claimed, "Funds have been claimed");

        uint256 amount = campaign.pledges[msg.sender];
        require(amount > 0, "No funds to refund");

        campaign.pledges[msg.sender] = 0;
        campaign.pledged -= amount;

        payable(msg.sender).transfer(amount);

        emit FundsRefunded(_id, msg.sender, amount);
    }

    function getCampaign(uint256 _id) public view 
    returns (
        address creator,
        uint256 goal,
        uint256 pledged,
        uint256 startTime,
        uint256 endTime,
        bool claimed,
        bool votingOpen,
        uint256 yesVotes,
        uint256 noVotes,
        bool exists
    ) {
        Campaign storage campaign = campaigns[_id];
        return (
            campaign.creator,
            campaign.goal,
            campaign.pledged,
            campaign.startTime,
            campaign.endTime,
            campaign.claimed,
            campaign.votingOpen,
            campaign.yesVotes,
            campaign.noVotes,
            campaign.exists
        );
    }

    function getPledgeAmount(uint256 _id, address _contributor) public view 
    returns (uint256) {
        return campaigns[_id].pledges[_contributor];
    }

    function getVoteStatus(uint256 _id, address _voter) public view 
    returns (bool) {
        return campaigns[_id].hasVoted[_voter];
    }

    function isExpired(uint256 _id) public view returns (bool) {
        return block.timestamp > campaigns[_id].endTime;
    }

    function timeRemaining(uint256 _id) public view returns (uint256) {
        Campaign storage campaign = campaigns[_id];
        if (block.timestamp >= campaign.endTime) {
            return 0;
        }
        return campaign.endTime - block.timestamp;
    }
}
